CREATE OR REPLACE TABLE ODS_SABRE.tb_tkt_document  
(
  ID_DATE_BQ STRING OPTIONS(description="Date of data load to the database."),
  Record_Indicator NUMERIC OPTIONS(description="A numeric value that identifies the table in which the row resides. All rows belonging to TktDocument have a value of 18 in this column."),
  PNRLocatorID STRING OPTIONS(description="Sabre PNR locator ID. Also known as the Host Record Locator."),
  PNRCreateDate DATE OPTIONS(description="Date that the PNR was created in the Sabre host system."),
  PrimaryDocNbr STRING OPTIONS(description="The document number including the carrier accounting code less the check digit and coupon numbers. If the document is conjunctive it is the first document number in the series."),
  VCRCreateDate DATE OPTIONS(description="Date that the ticket was issued . Date is stated according to agent's AAA city time zone - not Central Time. Value is stated as YYYY-MM-DD."),
  TransactionDate TIMESTAMP OPTIONS(description="Date and Time of the most recent modification to the document."),
  AirlineAccountingCode STRING OPTIONS(description="IATA assigned carrier accounting code."),
  EndDocNbr STRING OPTIONS(description="The final document number in a conjunctive document."),
  VendorName STRING OPTIONS(description="Name of the Validating Carrier."),
  PointOfTktIssuance STRING OPTIONS(description="Where the document was issued."),
  ValidatingVendorCode STRING OPTIONS(description="Two character IATA carrier code that issued the document."),
  ValidatingVendorNbr STRING OPTIONS(description="IATA assigned number corresponding to the ValidatingVendorCode"),
  PNRPurgeDate DATE OPTIONS(description="The date of the last indexed segment in which the PNR was purged from the source system."),
  CRSPNRLocator STRING OPTIONS(description="The identifier received from a GDS or other airline used to locate a PNR."),
  DocIssueDate DATE OPTIONS(description="Date the document was issued. Date is stated according to agent's AAA city time zone - not US Central Time."),
  CustomerFullName STRING OPTIONS(description="The ticketed passenger's full name. Format is Last/First where last name includes up to the first 27 characters of the last name."),
  AgentCountryCode STRING OPTIONS(description="The 2 character ISO country code of the creating city for the document."),
  IntlDocSaleCode STRING OPTIONS(description="Null for domestic travel."),
  TourCode STRING OPTIONS(description="Tour, Inclusive Tour (IT), Bulk Ticket (BT), Net Remit Contract Agreement Reference Code, Value Code or other code entered as part of the ticketing transaction."),
  AgentSine STRING OPTIONS(description="The agent sine code of the ticketing, or issuing, agent."),
  OwningCityCode STRING OPTIONS(description="The pseudo city code (PCC) of the booking agent which 'owns' the PNR at a particular point in time."),
  AAACityCode STRING OPTIONS(description="Pseudo City Code (PCC) or geographic city code of the issuing agent of the document."),
  DocIssueAAAIATANbr STRING OPTIONS(description="The IATA number of the issuing ticketing location."),
  HomeCityCode STRING OPTIONS(description="Pseudo City Code (PCC) to which the Ticketing agent is assigned in the agent's EPR."),
  Restrictions STRING OPTIONS(description="This column is no longer populated."),
  CurrConverRate STRING OPTIONS(description="The rate used to calculate the amounts on the ticket for currency conversion / rate of exchange."),
  BankSellRate INT64 OPTIONS(description="Indicates which Rate Table used to gain the CurrConverRate (1 for true that BSR was used for ticketing, 0 for false)."),
  BankBuyRate INT64 OPTIONS(description="Indicates which Rate Table used to gain the CurrConverRate (1 for true that BBR was used for ticketing, 0 for false)."),
  IntlClearHouseRate INT64 OPTIONS(description="Indicates which Rate Table used to gain the CurrConverRate (1 for true that ICH was used for ticketing, 0 for false)."),
  ExchgTktAmt NUMERIC OPTIONS(description="Amount of additional monies collected when a ticket is exchanged for another ticket."),
  SettlementAuthCode STRING OPTIONS(description="A 15-character reference number generated by the validating (ticketing) carrier authorizing settlement, or payment, of an electronic coupon."),
  BaseFareCurrCode STRING OPTIONS(description="The default ticketing currency code as stated in the agency's TJR or ticketing station."),
  BaseFareAmt NUMERIC OPTIONS(description="Amount of the base fare (without taxes) of all segments on the ticket."),
  TotalFareCurrCode STRING OPTIONS(description="Currency Code of the Total Amount on the ticket."),
  TotalDocAmt NUMERIC OPTIONS(description="Total price of this ticket document including base fare, taxes and fees."),
  EquivBaseFareCurrCode STRING OPTIONS(description="The currency in which the traveler paid for the ticket."),
  EquivBaseFareAmt NUMERIC OPTIONS(description="The base fare stated in the currency the traveler paid in."),
  DataInd STRING OPTIONS(description="Indicates the type of document issued."),
  FareCalc STRING OPTIONS(description="The text field that prints on the ticket document."),
  FareCalcType INT64 OPTIONS(description="A unique system-assigned numeric value representing the classification of fare calculation text."),
  OriginalIssueDate DATE OPTIONS(description="In the case of an exchange or reissue transaction, this is the date that the first (original)document was issued."),
  OriginalIssueCity STRING OPTIONS(description="In the case of an exchange or reissue transaction, this is the city where the first (original)document was issued."),
  OriginalIATANbr STRING OPTIONS(description="In the case of an exchange or reissue transaction, this is the IATA number of the location where the first (original)document was issued."),
  OriginalFOP STRING OPTIONS(description="In the case of an exchange or reissue transaction, this is the Form of Payment of the first (original)document that was issued."),
  OriginalTktNbr STRING OPTIONS(description="In the case of an exchange or reissue transaction, this is the first (original)document that was issued."),
  ExchgFOP STRING OPTIONS(description="The FOP of the most recently exchanged document."),
  AddlExchgTktData STRING OPTIONS(description="The ticket number of the other tickets used in the exchange."),
  ExchgCoupon STRING OPTIONS(description="This is the original Coupon Numbers of the primary/first exchanged ticket."),
  AutoPriceCode STRING OPTIONS(description="Coded value in the VCR which indicates whether a ticket was issued via auto/system pricing method, or manual/agent pricing method."),
  DocTypeCode STRING OPTIONS(description="A code which identifies the document type of the transaction."),
  DocStatusCode STRING OPTIONS(description="A- The document is active.  R - The document and all its coupons have been refunded. V - The document and all its coupons have been voided. X - The document and all its coupons have been exchanged for another ticket. E – The document has been partially exchanged."),
  PassengerType STRING OPTIONS(description="ATPCO standard code used in pricing and ticketing entries to identify the passenger."),
  SourceSystemId STRING OPTIONS(description="The host partition belonging to the hosted carrier."),
  OACStationNbr STRING OPTIONS(description="The OAC office code, or station number, associated with the host ticketing agent’s AAA location."),
  RefundableInd STRING OPTIONS(description="Indicates if the document is eligible for refund."),
  CommissionableInd STRING OPTIONS(description="Indicates if the AE Item is eligible for commission."),
  InterlineInd STRING OPTIONS(description="Indicates if Interline settlement is permitted."),
  EMDRFICode STRING OPTIONS(description="EMD Reason For Issuance Code."),
  EMDType STRING OPTIONS(description="A = Associated to a flight coupon S = Stand-Alone"),
  TaxExInd STRING OPTIONS(description="Tax exempt indicator."),
  ConsumedAtIssueInd STRING OPTIONS(description="Consumed at issue indicator."),
  ElectronicDocumentInd STRING OPTIONS(description="Indicates whether the document was issued electronically."),
  ManualDocumentInd STRING OPTIONS(description="Indicates whether the document was added manually via the manual document mask."),
  VoucherRefundInd STRING OPTIONS(description="Indicates whether a VOU or VCH type document is a refund."),
  RFICode STRING OPTIONS(description="Reason For Issuance Code."),
  RFITxt STRING OPTIONS(description="Reason For Issuance Text."),
  PTASvcChargeCurrCode STRING OPTIONS(description="Currency code for any service charges associated with Prepaid Ticket Advice (PTA)."),
  PTASvcChargeAmt NUMERIC OPTIONS(description="Amount for any service charges associated with PTAs."),
  PTAAddFundsDesc STRING OPTIONS(description="Description of any additional funds sent with a PTA."),
  PTAAddFundsCurrCode STRING OPTIONS(description="Currency code for additional funds sent with a PTA."),
  PTAAddFundsAmt NUMERIC OPTIONS(description="Amount of additional funds sent with a PTA."),
  ExchgChangeFeeCurrCode STRING OPTIONS(description="Currency code in which exchange fee for a ticket exchange transaction was paid."),
  ExchgChangeFeeAmt NUMERIC OPTIONS(description="Ticket exchange fee assessed in an exchange ticket transaction."),
  OtherFeeCurrCode STRING OPTIONS(description="Currency Code associated with 'Other' (OTH) type fee."),
  OtherFeeAmt NUMERIC OPTIONS(description="During an exchange transaction, amount associated with 'Other' (OTH) type fee that is charged by the carrier."),
  AddCollectCurrCode STRING OPTIONS(description="Currency code in which additional collection on an exchange transaction is paid"),
  AddCollectAmt NUMERIC OPTIONS(description="Amount of additional collection on an exchange ticket transaction"),
  OACDefaultPartitionCode STRING OPTIONS(description="The default carrier code from the station table used for this document issuance."),
  OACCityCode STRING OPTIONS(description="The account city of the agent who issued the document."),
  OACAcctCode STRING OPTIONS(description="The accounting code of the agent who issued the document."),
  NonRefFeeCurrCode STRING OPTIONS(description="Currency code in which nonrefundable fee for a ticket transaction was paid."),
  NonRefFeeAmt NUMERIC OPTIONS(description="Nonrefundable fee assessed in a ticket refund transaction."),
  OBFeeDescText STRING OPTIONS(description="OB Fee sub code description as defined by IATA or Airline in the pricing system"),
  OBFeePricingCode STRING OPTIONS(description="0 OB Fee Priced Code - Indicates how fee was priced."),
  OBFeeWaiverCode STRING OPTIONS(description="OB Fee Waiver Code – Waiver reason code as defined by the airline in MISCO* table"),
  OBFeeTotalCurrCode STRING OPTIONS(description="Total OB Fee amount in ticketing/charging currency."),
  OBFeeTotal NUMERIC OPTIONS(description="Total OB Fee amount charged."),
  OBFeeTotalUSD NUMERIC OPTIONS(description="Total OB Fee amount charged in USD using the ROE in the look up table at time of ticket issuance."),
  PricedPassengerType STRING OPTIONS(description="Passenger Type code used to price the itinerary."),
  OwnerCurrCode STRING OPTIONS(description="Default currency code applicable to the carrier that issued this ticketing document."),
  BaseFareOwnerAmt NUMERIC OPTIONS(description="Base fare on the ticketing document, expressed in the owning carrier’s currency."),
  BaseFareAmtUSD NUMERIC OPTIONS(description="Amount from BaseFareAmt column converted to USD using BSR applicable to date of ticket issuance."),
  TotalDocOwnerAmt NUMERIC OPTIONS(description="Total amount on the ticket, expressed in the owning carrier’s currency."),
  TotalDocAmtUSD NUMERIC OPTIONS(description="Total price of this ticket document including base fare, taxes and fees, converted to USD using the BSR applicable to date of ticket issuance.")
)
